import { useState, useEffect, useContext } from 'react'
import ThemeContext from './ThemeContext'
import useBreedList from './useBreedList'
import Results from './Results'
import getAnimals from './services/getAnimals'
import generateToken from './services/getToken'

const ANIMALS = ['bird', 'cat', 'dog', 'rabbit', 'horse']

const SearchParams = () => {
  const [animal, updateAnimal] = useState('')
  const [location, updateLocation] = useState('')
  const [breed, updateBreed] = useState('')
  const [pets, setPets] = useState([])
  const [animals, setAnimals] = useState([])
  const [breeds] = useBreedList(animal)
  const [theme, setTheme] = useContext(ThemeContext)

  useEffect(() => {
    getAnimals(setAnimals)
    requestPets()
  }, []) // eslint-disable-line react-hooks/exhaustive-deps

  async function requestPets() {
    const token = await generateToken()
    const res = await fetch(
      `https://api.petfinder.com/v2/animals?type=${animal}&breed=${breed}`,
      {
        method: 'GET',
        mode: 'cors',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      }
    )
    const json = await res.json()
    console.log(json.animals)
    setAnimals(json.animals)
  }

  return (
    <div className='search-params'>
      <form
        onSubmit={e => {
          e.preventDefault()
          requestPets()
        }}
      >
        <label htmlFor='location'>
          Location
          <input
            id='location'
            value={location}
            placeholder='Location'
            onChange={e => updateLocation(e.target.value)}
          />
        </label>
        <label htmlFor='animal'>
          Animal
          <select
            id='animal'
            value={animal}
            onChange={e => updateAnimal(e.target.value)}
            onBlur={e => updateAnimal(e.target.value)}
          >
            <option />
            {ANIMALS.map(animal => (
              <option key={animal} value={animal}>
                {animal}
              </option>
            ))}
          </select>
        </label>
        <label htmlFor='breed'>
          Breed
          <select
            disabled={!breeds.length}
            id='breed'
            value={breed}
            onChange={e => updateBreed(e.target.value)}
            onBlur={e => updateBreed(e.target.value)}
          >
            <option />
            {breeds.map((breed, idx) => (
              <option key={idx} value={breed.name}>
                {breed.name}
              </option>
            ))}
          </select>
        </label>
        <label htmlFor='theme'>
          Theme
          <select
            value={theme}
            onChange={e => setTheme(e.target.value)}
            onBlur={e => setTheme(e.target.value)}
          >
            <option value='peru'>Peru</option>
            <option value='darkblue'>Dark Blue</option>
            <option value='chartreuse'>Chartreuse</option>
            <option value='mediumorchid'>Medium Orchid</option>
          </select>
        </label>
        <button style={{ backgroundColor: theme }}>Submit</button>
      </form>
      <Results pets={pets} animals={animals} />
    </div>
  )
}

export default SearchParams
